import React from "react";
import Main from "./Main.js";
import Header from "./Header.js";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm.js";
import ImagePopup from "./ImagePopup.js";


function App() {
  //  Создание хуков
  const [isEditProfilePopupOpen, setEditProfilePopupOpen ] = React.useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen ] = React.useState(false);
  const [selectedCard, setSelectedCard] = React.useState(null);

  // Открытие попапов
  function handleEditAvatarClick(){
    setEditAvatarPopupOpen(true)
  }
  function handleEditProfileClick(){
    setEditProfilePopupOpen(true)
  }
  function handleAddPlaceClick(){
    setAddPlacePopupOpen(true)
  }
  function handleCardClick(item){
    setSelectedCard(item)
  }
  //   Закрытие попапов
  function closeAllPopups(){
    const buttonClosePopup = document.querySelectorAll('.popup__close-button');
    buttonClosePopup.forEach((el) => {
      el.addEventListener('click', () => {
        setEditAvatarPopupOpen(false)
        setEditProfilePopupOpen(false)
        setAddPlacePopupOpen(false)
        setSelectedCard()
      })
    })
  }
  return (
    <div className="page">
      <Header />
      <Main 
        onEditProfile={handleEditProfileClick} 
        onAddPlace={handleAddPlaceClick} 
        onEditAvatar={handleEditAvatarClick}
        onCardClick={handleCardClick}
      />
      <Footer />
      <PopupWithForm name="Prof" title ="Редактировать профиль" button="Сохранить" isOpen = {isEditProfilePopupOpen} onClose={closeAllPopups}>
        <input className="form__input" placeholder="Ваше Имя" id="form__name" name="name" required type="text" minLength="2" maxLength="40" />
        <span className="form__input-error form__name-error"></span>
        <input className="form__input" placeholder="Чем вы занимаетесь" id="form__about" name="about" required type="text" minLength="2" maxLength="200" />
        <span className="form__input-error form__about-error"></span>
      </PopupWithForm>
      <PopupWithForm name="Place" title ="Новое место" button="Создать" isOpen = {isAddPlacePopupOpen} onClose={closeAllPopups}>
        <input className="form__input" placeholder="Название" id="form__place" name="place" required type="text" minLength="2" maxLength="30"/>
        <span className="form__input-error form__place-error"></span>
        <input className="form__input" placeholder="Ссылка на картинку" id="form__link" name="link" required type="url"/>
        <span className="form__input-error form__link-error"></span>
      </PopupWithForm>
      <ImagePopup 
        card={selectedCard} 
        onClose={closeAllPopups}
        />
      <PopupWithForm name="Delet" title="Вы уверены?" button="Да" onClose={closeAllPopups}/>
      <PopupWithForm name="EditAvatar" title="Обновить аватар" button="Сохранить" isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups}>
        <input className="form__input" placeholder="Ссылка на новый аватар" id="form__input" name="avatar" margin="0" required type="url" />
        <span className="form__input-error form__link-error"></span>
      </PopupWithForm>

      <template id="templateCard">

      </template>
    </div>
  );
}

export default App;
