import penPath from "../images/pen.svg"
import plusPath from "../images/plus.svg"

function Main() {
    function handleEditAvatarClick(){
        const buttonEditProf = document.querySelector('.profile__change');
        const popupEditProf = document.querySelector('#popupEditAvatar');
        buttonEditProf.addEventListener('click', () => {
          popupEditProf.classList.add('popup_opend');
        })
      }
    return(
        <main>
            <section className="profile">
                <div className="profile__personal">
                    <img src="https://pictures.s3.yandex.net/frontend-developer/common/ava.jpg" alt="Фото профиля" className="profile__photo"/>
                    <button className="profile__photo-edit" type="button"></button>
                    <div className="profile__info">
                        <h1 className="profile__name"></h1>
                        <button type="button" className="profile__change" onClick={handleEditAvatarClick()}>
                            <img src={penPath} alt="Изменить профиль"/>
                        </button>
                        <p className="profile__about"></p>
                    </div>
                </div>
                <button type="button" className="profile__add">
                    <img src={plusPath} alt="Кнопка добавить" className="profile__plus"/>
                </button>
            </section>
            <section className="elements">
            </section>
        </main>
    )
}

export default Main
