import React, {useState} from 'react';
import {Link, useNavigate} from 'react-router-dom';
import * as auth from '../utils/Auth';

function Register() {
    const [formValue, setFormValue] = useState({
      email: '',
      password: '',
    })
    const navigate = useNavigate();

    const handleChange = (e) => {
      const {name, value} = e.target;
      setFormValue({
        ...formValue,
        [name]: value
      });
    }
    const handleSubmit = (e) => {
      e.preventDefault();
      auth.register(  formValue.email, formValue.password)
        .then((res) => {
          navigate('/sign-in', {replace: true});
          console.log(res)
      })
    }

    return(
      <form className="register">
        <h1 className='register__title'>Регистрация</h1>
        <input className='register__input' id="email" placeholder='Email' name="email" type="email" ></input>
        <input className='register__input' id="password" placeholder='Пароль' name="password" type="password" ></input>
        <button className='register__button' type="submit" >Зарегистрироваться</button>
        <div className='register__signin'>
            <p className='register__login-text'>Уже зарегистрированы?</p>
            <Link to="/sign-in" className='register__login-link'>Войти</Link>
        </div>
      </form>
    )
  }

export default Register
