import PopupWithForm from "./PopupWithForm.js";
import React, { useRef, useState } from "react";
import {CurrentUserContext} from "../contexts/CurrentUserContext"

function EditAvatarPopup(props){



    return(
        <PopupWithForm name="EditAvatar" title="Обновить аватар" isOpen={isOpen} onClose={onClose}> 
            <input className="form__input" placeholder="Ссылка на новый аватар" id="form__input" name="avatar" margin="0" required type="url" /> 
            <span className="form__input-error form__link-error"></span> 
        </PopupWithForm> 
    )
}
