import React, {useState} from 'react';
import {useNavigate} from 'react-router-dom';
import * as auth from './Auth.js';


function Login({handleLogin}) {

  const [formValue, setFormValue] = useState({
    useremail: '',
    password: ''
  })
  const navigate = useNavigate();

  const handleChange = (e) => {
    const {name, value} = e.target;

    setFormValue({
      ...formValue,
      [name]: value
    });
  }
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formValue.useremail || !formValue.password){
      return;
    }
    auth.authorize(formValue.useremail, formValue.password)
      .then((data) => {
        console.log(data)
        if (data.token){
          setFormValue({useremail: '', password: ''});
          handleLogin();
          navigate('/', {replace: true});

        }
      })
      .catch(err => console.log(err));
  }

    return(
      <form className="login" onSubmit={handleSubmit}>
        <h1 className='login__title'>Вход</h1>
        <input className='login__input' placeholder='Email' name ="useremail" type="email" required value={formValue.useremail} onChange={handleChange}></input>
        <input className='login__input' placeholder='Пароль' name ="password" type ="password" required value={formValue.password} onChange={handleChange}></input>
        <button className='login__button' type='submit'>Войти</button>
      </form>
    )
}


export default Login;
