import React from "react"
import penPath from "../images/pen.svg"
import plusPath from "../images/plus.svg"
import api from "../utils/Api"

function Main(props) {

    const [userName, setUserName] = React.useState("");
    const [userDescription, setUserDescription] = React.useState("");
    const [userAvatar, setUserAvatar] = React.useState("");
    const [cards, setCards] = React.useState([]);
    React.useEffect(() => {
        api.getProfile()
            .then((data) => {
                setUserName(data.name);
                setUserDescription(data.about)
                setUserAvatar(data.avatar)
            });
        api.getCards()
            .then((data) => {
                setCards(
                    data.map((item) => ({
                        id: item._id,
                        src: item.link,
                        alt: item.name,
                        title: item.name,
                        likes: item.likes.length,
                    }))
                )
            })
    });
    return(
        <main>
            <section className="profile">
                <div className="profile__personal">
                    <img src={userAvatar} alt="Фото профиля" className="profile__photo"/>
                    <button className="profile__photo-edit" type="button" onClick={props.onEditAvatar}></button>
                    <div className="profile__info">
                        <h1 className="profile__name">{userName}</h1>
                        <button type="button" className="profile__change" onClick ={props.onEditProfile} >
                            <img src={penPath} alt="Изменить профиль"/>
                        </button>
                        <p className="profile__about">{userDescription}</p>
                    </div>
                </div>
                <button type="button" className="profile__add" onClick ={props.onAddPlace}>
                    <img src={plusPath} alt="Кнопка добавить" className="profile__plus"/>
                </button>
            </section>
            <section className="elements">
                {
                    cards.map(({id, ...props}) => {

                    })
                }
                <div className="element">
                    <button className="element__img-popup">
                        <img className="element__img" alt="close"/>
                    </button>
                    <button type="button" className="element__delete" id="deletButton"></button>
                    <div className="element__name">
                        <h2 className="element__paragraph"></h2>
                        <div className="element__like-zone">
                            <button type="button" className="element__like"></button>
                            <span className="element__counter">0</span>
                        </div>
                    </div>
                </div>
            </section>
        </main>
    );
}

export default Main
