import PopupWithForm from "./PopupWithForm.js";
import React, { useRef, useState } from "react";
import {CurrentUserContext} from "../contexts/CurrentUserContext"

function EditAvatarPopup(props) {
    const currentUser = React.useContext(CurrentUserContext);
    const [avatar, setAvatar] = useState(currentUser.avatar);
    const avatarRef = React.useRef()


    function changeAvatar(e) {
        setAvatar(e.target.value);
    }
    function handleSubmit(e) {
        e.preventDefault();

        props.onUpdateAvatar({
          avatar: avatarRef.current.avatar,
        });
        console.log(avatar)
      } 
    return( 
        <PopupWithForm name="EditAvatar" title="Обновить аватар" isOpen={props.isOpen} onClose={props.onClose} onSubmit={handleSubmit}>
          <input className="form__input" placeholder="Ссылка на новый аватар" id="form__input" name="avatar" margin="0" required type="url" defaultValue={''} ref={avatarRef} onChange={changeAvatar}/>
          <span className="form__input-error form__link-error"></span>
        </PopupWithForm>
    )
}

export default EditAvatarPopup
