import {useState, useEffect} from 'react';
import Main from "./Main.js";
import Header from "./Header.js";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm.js";
import EditProfilePopup from './EditProfilePopup.js';
import EditAvatarPopup from './EditAvatarPopup.js';
import ImagePopup from "./ImagePopup.js";
import api from "../utils/Api";
import {CurrentUserContext, user} from "../contexts/CurrentUserContext"
import React from 'react';


function App() {
  //  Создание хуков
  const [isEditProfilePopupOpen, setEditProfilePopupOpen ] = useState(false);
  const [isAddPlacePopupOpen, setAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setEditAvatarPopupOpen ] = useState(false);
  const [selectedCard, setSelectedCard] = useState(null);
  const [currentUser, setCurrentUser] = useState(user);
  const [cards, setCards] = useState([]);
  const [avatar, setAvatar] = useState();

  useEffect(() => {
    api.getProfile()
      .then((data) => {
        setCurrentUser(data);
      })
  }, [])

  function handleCardLike(card) {
    const isLiked = card.likes.some(i => i._id === currentUser._id);
    if(!isLiked){
      api.putLike(card._id)
        .then((res) => {
          setCards((state) => state.map((i) => i._id === card._id ? res : i))
        })
    }
    else{
      api.deletLike(card._id)
        .then((res) => {
          setCards((state) => state.map((i) => i._id === card._id ? res : i))
        })
    }
  } 

  function handleCardDelete(card){  
    api.deletCard(card._id)
      .then((res) => {
        setCards((state) => state.filter((i) => i._id !== card._id ));        
      })
  }

  function handleUpdateUser(info){
    api.changeProfile(info)
      .then((res) => {
        setCurrentUser(res)
        closeAllPopups()
      })
  }
  function handleUpdateAvatar(info){
    api.changeAvatar(info)
      .then((res) => {
        setAvatar(res.avatar)
        console.log(avatar)
      })
  }
  // Открытие попапов
  function handleEditAvatarClick(){
    setEditAvatarPopupOpen(true)
  }
  function handleEditProfileClick(){
    setEditProfilePopupOpen(true)
  }
  function handleAddPlaceClick(){
    setAddPlacePopupOpen(true)
  }
  function handleCardClick(item){
    setSelectedCard(item)
  }
  //   Закрытие попапов
  function closeAllPopups(){
    setEditAvatarPopupOpen(false)
    setEditProfilePopupOpen(false)
    setAddPlacePopupOpen(false)
    setSelectedCard(null)
  }
  return (
    < CurrentUserContext.Provider value={currentUser}>
      <div className="page">
        <Header />
        <Main 
          onEditProfile={handleEditProfileClick} 
          onAddPlace={handleAddPlaceClick} 
          onEditAvatar={handleEditAvatarClick}
          onCardClick={handleCardClick}
          setCards={setCards}
          cards={cards}
          onCardLike={handleCardLike}
          onCardDelete={handleCardDelete}
        />
        <Footer /> 
        <EditProfilePopup 
          isOpen={isEditProfilePopupOpen} 
          onClose={closeAllPopups}
          onUpdateUser={handleUpdateUser}
        />
        <PopupWithForm name="Place" title ="Новое место" button="Создать" isOpen = {isAddPlacePopupOpen} onClose={closeAllPopups}>
          <input className="form__input" placeholder="Название" id="form__place" name="place" required type="text" minLength="2" maxLength="30"/>
          <span className="form__input-error form__place-error"></span>
          <input className="form__input" placeholder="Ссылка на картинку" id="form__link" name="link" required type="url"/>
          <span className="form__input-error form__link-error"></span>
        </PopupWithForm>
        <ImagePopup 
          card={selectedCard} 
          onClose={closeAllPopups}
          />
        <PopupWithForm name="Delet" title="Вы уверены?" button="Да" onClose={closeAllPopups}/>
        <EditAvatarPopup 
          isOpen={isEditAvatarPopupOpen} 
          onClose={closeAllPopups} 
          onUpdateAvatar={handleUpdateAvatar}
        /> 
        <template id="templateCard">

        </template>
      </div>
    </CurrentUserContext.Provider>

  );
}

export default App;
